import './index.scss';
import AnimatedLetters from '../AnimatedLetters';
import { useState } from 'react';
import { useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAndroid, faAngular, faCss3, faGit, faGithub, faHtml5, faJsSquare, faReact } from '@fortawesome/free-brands-svg-icons';
import { faGift } from '@fortawesome/free-solid-svg-icons';
import Loader from 'react-loaders';
import Sidebar from '../Sidebar';

const About = () => {

    const[letterClass, setLetterClass] = useState('text-aimmation');
    
    useEffect(() => {
        setTimeout(() => {
            setLetterClass('text-animate-hover')
        }, 3000)
    }, [])
    
    return(
        <>
        <div className='container about-page'>
                <div className='text-zone'>
                <h1>
                    <AnimatedLetters 
                        strArray={["A", 'b', 'o', 'u', 't', ' ', 'm', 'e']}
                        idx={15}
                    />    
                    {/* // index 15 means a delay of 1.5s */}
                </h1>
                <p>
                    I'm very ambitious full-stack web-developer looking for a role in
                    established IT company with the opportunity to work with the latest
                    technologies on challenging and diverse projects.
                </p>
                <p>
                    I'm quietly confident, naturally curious, and perpetually working on improving my
                    chops one design problem at a time.
                </p>
                <p>
                    If I need to define myself in one sentence that would be a family person,
                    grateful for all the things which my parents did for me!
                </p>
                </div>

                <div className='stage-cube-cont'>
                <div className='cubespinner'>
                    <div className='face1'>
                        <FontAwesomeIcon icon={faHtml5} color="#F06529"/>
                    </div>
                    <div className='face2'>
                        <FontAwesomeIcon icon={faAngular} color="#DD0031"/>
                    </div>
                    <div className='face3'>
                        <FontAwesomeIcon icon={faJsSquare} color="#EFD81D"/>
                    </div>
                    <div className='face4'>
                        <FontAwesomeIcon icon={faReact} color="#5ED4F4"/>
                    </div>
                    <div className='face5'>
                        <FontAwesomeIcon icon={faGithub} color="#EC4D28"/>
                    </div>
                    <div className='face6'>
                        <FontAwesomeIcon icon={faAndroid} color="#669933"/>
                    </div>

                </div>
                </div>
            </div>
            <Loader type='pacman' />
        </>
    )
}
export default About;
